mapper.select:
SELECT bookName, authorName, bookPublishDate, bookCost, bookLikeCount FROM book LEFT JOIN author ON bookIdPk = authorIdPk WHERE bookIdPk NOT IN(#{pagingDto.lastId}) AND bookCategory = #{bookCategory}
AND #{pagingDto.pagingCursor} >= bookPublishDate ORDER BY bookPublishDate
AND #{pagingDto.pagingCursor} >= bookLikeCount ORDER BY bookLikeCount
DESC LIMIT #{pagingDto.onePageLimit};
